(window.webpackJsonp=window.webpackJsonp||[]).push([[17],{78:function(n,t){n.exports="## OstToast 示例\n\n  \n```jsx\nimport OstToast from 'ost-ui';\n\n:::$demo\n    constructor(props, context) {\n        super(props, context);\n        this.state = {text: ''};\n    }\n\n    show = () => {\n        OstToast.show(this.state.text, 2);\n    }\n\n    render() {\n       return(\n            <div className='ost-toast-demo'>\n                <input\n                    className='ost-toast-demo-input'\n                    onChange={e => this.setState({text: e.currentTarget.value})}\n                    type='text'/>\n                <button\n                    className='ost-toast-demo-button'\n                    onClick={this.show}>展示Toast</button>\n            </div>\n       )\n    }\n:::$\n```\n\n```css\n<style>\n.ost-toast-demo {\n    width: 100%;\n    height: 100%;\n}\n\n.ost-toast-demo-input {\n    width: 80%;\n    height: 44px;\n    font-size: 14px;\n    color: #666;\n    margin: 10px 10%;\n}\n\n.ost-toast-demo-button {\n    width: 80%;\n    height: 46px;\n    font-size: 14px;\n    background: #FF6D00;\n    color: #fff;\n    margin: 10px 10%;\n}\n\n</style>\n\n```\n"}}]);